import React, { useEffect, useState } from 'react';
import Combo from './components/combo';
import data from './json/data.json';
import getEvolutions from './functions/getEvolutions';
import ProfileGroup from './components/profileGroup';
import Profile from './components/profile';

export default function App() {
    const [selectedType, setSelectedType] = useState("진화 상태");
    const [types, setTypes] = useState(["유년기1", "유년기2", "성장기", "성숙기", "완전체"]);
    const [all, setAll] = useState(data);
    const [filtered, setFiltered] = useState([]);
    const [selectedDigimon, setSelectedDigimon] = useState({ from: "" });

    useEffect(() => {
        verticalLeftLength();
        console.log(selectedDigimon);
    }, [selectedDigimon]);

    const selectDigimon = (digimon) => {
        getEvolutions(digimon);
        setSelectedDigimon(digimon);
    }

    const changeType = (type) => {
        setSelectedType(type);
        setSelectedDigimon({ from: "디지몬 선택"});
        const temp = new Array();
        let prev = "";
        for(const each of all) {
            if(each.from === prev) continue;
            if(each.type !== type) continue;

            prev = each.from;
            temp.push(each);
        }
        setFiltered(temp);
    }

    const verticalLeftLength = () => {
        const map = selectedDigimon?.down?.flatMap(each => each?.down);
        let topPadding = 0;
        let bottomPadding = 0;
        let length = 0;
        for(let i = 0; i < map.down?.length; i++) {
            const each = map.down[i];
            length += each?.down?.length * 89 ?? 0;
        }
        console.log(selectedDigimon?.down?.flatMap(each => each?.down));
    }
    const verticalHeightLeft = { height: `calc(var(--profile-height) * ${selectedDigimon?.down?.length - 1})` };

    return (
        <div id="container">
            <div className="header">header bar & logo</div>
            {/* 진화 상태와 그에 맞는 디지몬 선택자 */}
            <div className='main'>
                <h1 className="title-message">진화 상태에 맞는 디지몬을 선택하세요.</h1>
                <div className="filters">
                    <Combo list={types} selected={selectedType} select={changeType} selectedType={selectedType}></Combo>
                    <Combo list={filtered} selected={selectedDigimon.from} select={(current) => selectDigimon(current)}  selectedType={selectedType}></Combo>
                </div>
                <div className='revolution'>
                    { selectedDigimon?.down?.length > 0 && <ProfileGroup digimons={selectedDigimon.down} direction={"right"}></ProfileGroup> }
                    { selectedDigimon?.down?.length > 1 && <span className="vertical-line right" style={verticalHeightLeft}></span> }
                    { selectedDigimon?.down?.length > 1 && <span className="line half"></span> }
                    { selectedDigimon.from !== "" && <Profile digimon={selectedDigimon} ></Profile>}
                    { selectedDigimon?.up?.length > 0 && <ProfileGroup digimons={selectedDigimon.up} direction={"left"}></ProfileGroup> }
                </div>
            </div>
            <div className="footer">푸터</div>
        </div>
    );
}
