import React, { useState } from "react";

export default function Combo({ list, selected, select }) {
    console.log(list);
    const [isActivated, setIsActivated] = useState(false);

    const toggleList = () => setIsActivated(!isActivated);
    const selectOption = (option) => {
        select(option);
        console.log(option)
    }
    
    return (
        <div className="combo" onClick={toggleList}>
            <div className="selected">{selected}</div>
            <div className={`list ${isActivated ? "active" : ""}`}>
                {
                    list.length && list.map(element => (
                        <button type="button" onClick={() => selectOption(element)} key={`${element}_option`} className="combo-button">{element}</button>
                    ))
                }
            </div>
            <div className={`triangle ${isActivated ? "active" : ""}`}>â–¼</div>
        </div>
    );
}
