import React from "react";
import ProfileWithLine from "./profileWithLine";
import Profile from "./profile";

export default function ProfileGroup({ digimons, direction }) {
    const flexRow = {flexDirection: "row"};
    const flexColumn = {flexDirection: "column"};

    // digimons의 요소가 1개인 경우
    if(digimons?.length === 1) {
        // digimons.down이 존재하는 경우 -> profileGroup
        if(digimons[0].down !== undefined || digimons[0].down !== null) {
            console.log("digimons1", digimons)
            return (<div className="profile-group" style={flexRow}>
                        <ProfileGroup digimons={digimons[0].down} direction={direction}></ProfileGroup>
                        <Profile digimon={digimons[0]} ></Profile>
                    </div>);
        } else {
            // digimons.down이 존재하지 않는 경우 -> profile
            console.log("digimons2", digimons)
            return (<div className="profile-group" style={flexRow}>
                        <Profile digimon={digimons[0]} ></Profile>
                    </div>);
        }
    // digimons의 요소가 복수인 경우
    } else if(digimons?.length > 1) {
        console.log("digimons3", digimons)
        return (
            <div className="profile-group" style={flexColumn}>
                { digimons.map(digimon => {
                if(digimon.down === undefined || digimon.down === null) {
                    return (
                        <Profile digimon={digimon}></Profile>
                    );
                } else if(direction === "right") {
                    return (<div className="profile-group">
                                <ProfileGroup digimons={digimon.down} direction={direction}></ProfileGroup>
                                <Profile digimon={digimon}></Profile>
                            </div>);
                } else {
                    return (<ProfileGroup digimons={digimon.down} direction={direction}></ProfileGroup>);
                }
                }) }
            </div>
        );



        // digimons의 각 요소 digimon에 digimon.down이 존재하는 경우 -> profileGroup
        if(digimons.down !== undefined) {
            console.log("digimons3", digimons)
            return digimons.map(digimon => (
                <div className="profile-group">
                    <ProfileGroup digimons={digimon.down} direction={direction}></ProfileGroup>
                    <Profile digimon={digimon}></Profile>
                </div>
            ));
        } else {
            // digimons.down이 존재하지 않는 경우 -> profile
            console.log("digimons4", digimons)
            return (<div className="profile-group">
                        { digimons.map(digimon => <Profile digimon={digimon}></Profile>) }
                    </div>);
        }

        return digimons.map(digimon => <Profile digimon={digimon}></Profile>);
    }

    return (<div className="profile-group"></div>);

    // let tags;
    
    // if(digimons.length > 0) return (<div className="profile-group"></div>);
    // else {
    //     tags += (<div className="profile-group">
    //         { digimons.length > 0 && digimons.map(digimon => (
    //             <ProfileWithLine digimon={digimon} direction={direction} length={digimons.length > 1 ? "full" : "half"}></ProfileWithLine>
    //         ))}
    //     </div>)
    // }
    return (
        <div className="profile-group">
            { digimons.length === 1 && <Profile digimon={digimons[0]} ></Profile>}
            { digimons.length > 1 && digimons.map(digimon => <Profile digimon={digimon}></Profile> )}
            {/* { digimons.length > 1 && 
                digimons.filter(digimon => digimon.down !== undefined && digimon.down?.length > 0)
                        .map(digimon => <ProfileGroup digimons={digimon.down} direction={direction}></ProfileGroup>)} */}
        </div>
    );
}
