import React, { useEffect, useState } from "react";

export default function ProfileLine({ digimon, direction }) {
    const PROFILE_HEIGHT = 80;
    const [heights, setHeights] = useState([]);

    useEffect(() => {
        if(digimon.down !== undefined || digimon.down !== null)
        digimon.down.forEach(d => console.log(d.from, getDownScore(d)));
    }, []);

    const getDownScore = (digimon) => {
        if(digimon.down === undefined || digimon.down === null) return 1;
        if(digimon.down.length === 1) return 1;
        if(digimon.type === "유년기2") return digimon.down?.length;
        if(digimon.type === "성장기") {
            // 유년기2(digimon.down 내 각각)의 down.length의 합산 / 2
            let acc = 0;
            digimon.down.forEach(d => {
                // console.log(digimon.from, d.down.length);
                acc += d.down?.length || 0;
            });
            return acc;
        }
    }

    const getPercent = (d) => Number.isInteger(d.percentage * 100) ? 
                            d.percentage * 100 :
                            (d.percentage * 100).toFixed(2);

    const lineStyle = {stroke:"#e6e6e6", strokeWidth: 5 + "px", strokeLinecap: "round"};

    const getPercentTop = (d) => {
        if(direction === "right") {
            const i = digimon?.down.findIndex(d2 => d2.from === d.from);
            return 80 * (i+1) - 60;
        }
    }

    return (
        <div className="line-wrapper">
            { digimon.down?.map(d => <span style={{position: "absolute", top:`${getPercentTop(d)}px`, left: `${0}px`}}>{`${getPercent(d)}%`}</span>) }
            <svg width={100} height={PROFILE_HEIGHT * getDownScore(digimon)}>
                {/* right 방향 */}
                { digimon.down?.map((d, i) => {
                    return <line x1={0} y1={80 * (i+1) - 40} x2={50} y2={80 * (i+1) - 40} style={lineStyle}>
                    </line>
                })}
                { digimon.down?.length > 1 && <line x1={50} y1={42} x2={50} y2={80 * digimon.down.length - 40} style={lineStyle}></line>}
                <line x1={50} y1={80 * (digimon.down?.length / 2)} x2={100} y2={80 * digimon.down?.length / 2} style={lineStyle}></line>
            </svg>
            {/* <span>{percent}%</span> */}
            {/* <svg width={55} height={10} style={{transform: "rotateY(180deg)"}}>
                <line x1={0} y1={5} x2={50} y2={5} style={lineStyle}></line>
            </svg> */}
            {/* <span className={`line ${length}`}></span> */}
        </div>
    );
}
