import React from "react";

export default function ProfileLine({ digimon, direction }) {
    const getPercent = (d) => Number.isInteger(d.percentage * 100) ? 
                            d.percentage * 100 :
                            (d.percentage * 100).toFixed(2);

    const lineStyle = {stroke:"#e6e6e6", strokeWidth: 5 + "px", strokeLinecap: "round"};
    const svgHeight = direction === "right" ? 85 * digimon?.down.length : 85 * digimon?.up.length;

    return (
        <div className="line-wrapper">
            { digimon.down.map(d => <span>{`${getPercent(d)}%`}</span>) }
            <svg width={100} height={svgHeight}>
                {/* right 방향 */}
                { digimon.down.map((d, i) => {
                    return <line x1={0} y1={85 * (i+1) - 34} x2={50} y2={85 * (i+1) - 34} style={lineStyle}>
                    </line>
                })}
                { digimon.down.length > 1 && <line x1={50} y1={51} x2={50} y2={85 * digimon.down.length - 34} style={lineStyle}></line>}
                <line x1={50} y1={85 * (Math.ceil(digimon.down.length / 2)) - 34} x2={100} y2={85 * (Math.ceil(digimon.down.length / 2)) - 34} style={lineStyle}></line>
            </svg>
            {/* <span>{percent}%</span> */}
            {/* <svg width={55} height={10} style={{transform: "rotateY(180deg)"}}>
                <line x1={0} y1={5} x2={50} y2={5} style={lineStyle}></line>
            </svg> */}
            {/* <span className={`line ${length}`}></span> */}
        </div>
    );
}
