import React, { useState } from "react";

export default function Combo({ list, selected, select }) {
    console.log(list);
    const [isActivated, setIsActivated] = useState(false);

    const activeList = () => setIsActivated(!isActivated);

    return (
        <div className="combo" onClick={activeList}>
            <div className="selected" onClick={select}>{selected}</div>
            <div className={`list ${isActivated ? "active" : ""}`}>
                {
                    list.length && list.map(element => (
                        <button type="button" key={`${element}_option`} className="combo-button">{element}</button>
                    ))
                }
            </div>
            <div className={`triangle ${isActivated ? "active" : ""}`}>â–¼</div>
        </div>
    );
}
